<?php//map tiles configuration$Tile_config = array(	'width'=>0.02,	'height'=>0.02,	'tileWidth'=>1024,	'tileHeight'=>1024,	'criticalPxLength'=> 20,//px	'criticalPxArea'=>400,//px	'zoomLevel'=>array(0.0625, 0.125, 0.25, 0.5, 1, 2, 4, 8),	'lineZoomValueArray' => array(),	'areaZoomValueArray' => array(),	'timeout'=>120 //file request timeout second);function initTileConfig(){	global $Tile_config;	$Tile_config['criticalGeoLength'] = $Tile_config['width']*$Tile_config[criticalPxLength]/$Tile_config['tileWidth'];	$Tile_config['criticalGeoArea'] = $Tile_config['width']*$Tile_config['height']*$Tile_config['criticalPxArea']/($Tile_config['tileWidth']*$Tile_config['tileHeight']);	$l = count($Tile_config['zoomLevel']);	for($i=0;$i<$l;$i++)	{		$zl = $Tile_config['zoomLevel'][$i];		array_push($Tile_config['lineZoomValueArray'], $Tile_config['criticalGeoLength']/$zl);		array_push($Tile_config['areaZoomValueArray'], $Tile_config['criticalGeoArea']/pow($zl, 2));	}}initTileConfig();?>