<?phpinclude("osm2geo.php");function getTileToJson($x1, $y1, $x2, $y2, $Tile_config){	$baseUrl = "http://www.openstreetmap.org/api/0.6/map?bbox=";	$url = $baseUrl.$x1.','.$y1.','.$x2.','.$y2;	//*****************************cURL 扩展库**********************************	$ch = curl_init();	$timeout = 300;	curl_setopt ($ch, CURLOPT_URL, $url);	curl_setopt ($ch, CURLOPT_RETURNTRANSFER, 1);	curl_setopt ($ch, CURLOPT_CONNECTTIMEOUT, $timeout);	$osm = curl_exec($ch);	curl_close($ch);		//********************** to json file *******************************	if($osm)	{		//file_put_contents("../data/osm_".($x1/$Tile_config['width'])."_".(($y1/$Tile_config['height'])).".osm", $osm);//save file		$xml = simplexml_load_string($osm);		$geojson = osm2geo($xml);		$result = json_encode($geojson);		file_put_contents("../data/tile_".($x1/$Tile_config['width'])."_".(($y1/$Tile_config['height'])).".json", $result);//save file		return $result;	}	else	{		return false;	}}?>